public class Learning {    public static void print(int num){        for(int i = 31 ; i >= 0; i--){            System.out.print((num & (1 << i)) == 0 ? "0" : "1");        }        System.out.println();    }    public static void main(String[] args) {        int a = 2;        int b = -3;        int c = Integer.MIN_VALUE;        System.out.println(c);        System.out.println(-c);        print(~c);    }}