/** * ClassName: Learning * Package: PACKAGE_NAME * Description: * * @Author CYT * @Create 2024/4/17 19:40 * @Version 1.0 */public class Learning {    public static void print(int num){        for(int i = 31 ; i >= 0; i--){            System.out.print((num & (1 << i)) == 0 ? "0" : "1");        }        System.out.println();    }    public static void main(String[] args) {        int a = 2;        int b = 3;        print(a);        print(b);        System.out.println(a&b);        print(a&b);    }}