class Add {    public static void add(){        ListNode l2 = new ListNode(4);        ListNode l1 = new ListNode(3,l2);        System.out.println(l1.val);    }    public static void main(String[] args) {        add();    }}public class ListNode{    int val;    ListNode next;    ListNode(){}    ListNode(int val){        this.val = val;    }    ListNode(int val,ListNode next){        this.val = val;        this.next = next;    }}